/* BBMM Settings Inspector + Flags Browser ==================================
		- Sources: Settings | User Flags (Me) | User Flags (All Users, GM)
		- Namespace dropdown adapts to chosen source
		- Batching + lazy pretty
========================================================================== */
(async () => {
	const api = game.modules.get("bbmm")?.api;
	if (!api) return ui.notifications.error("BBMM API not available.");
	api.openNamespaceInspector();
})();
